import { useState, useEffect } from "react";
import Layout from "@/components/layout/Layout";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { AlertCircle, Plus, Trash2 } from "lucide-react";
import { Slider } from "@/components/ui/slider";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Skeleton } from "@/components/ui/skeleton";

type MealItem = {
  id: number;
  dietMealId: number;
  name: string;
  quantity: string;
  calories: number;
  protein: number;
  carbs: number;
  fat: number;
};

type Meal = {
  id: number;
  dietDayId: number;
  type: string;
  time: string;
  notes?: string;
  items: MealItem[];
};

type DietDay = {
  id: number;
  userId: number;
  date: string;
  totalCalories: number;
  totalProtein: number;
  totalCarbs: number;
  totalFat: number;
  waterIntake: number;
  notes?: string;
  meals: Meal[];
};

const mealItemSchema = z.object({
  name: z.string().min(1, "Food item name is required"),
  quantity: z.string().min(1, "Quantity is required"),
  calories: z.number().min(0, "Calories must be a positive number"),
  protein: z.number().min(0, "Protein must be a positive number"),
  carbs: z.number().min(0, "Carbs must be a positive number"),
  fat: z.number().min(0, "Fat must be a positive number"),
});

const mealSchema = z.object({
  type: z.enum(["breakfast", "lunch", "dinner", "snack"], {
    required_error: "Please select a meal type",
  }),
  time: z.string().min(1, "Time is required"),
  notes: z.string().optional(),
});

type MealItemFormValues = z.infer<typeof mealItemSchema>;
type MealFormValues = z.infer<typeof mealSchema>;

export default function DietRoutine() {
  const { toast } = useToast();
  const [currentDate, setCurrentDate] = useState<string>(new Date().toISOString().split('T')[0]);
  const [activeMealId, setActiveMealId] = useState<number | null>(null);
  const [isAddingMeal, setIsAddingMeal] = useState(false);
  const [isAddingMealItem, setIsAddingMealItem] = useState(false);
  const [waterIntake, setWaterIntake] = useState(0);

  const { data: dietDay, isLoading } = useQuery<DietDay | null>({
    queryKey: ['/api/diet', currentDate],
    queryFn: async () => {
      const response = await fetch(`/api/diet/${currentDate}`, {
        credentials: 'include',
      });
      if (!response.ok) {
        if (response.status === 404 || response.status === 401) return null;
        throw new Error('Failed to fetch diet');
      }
      return response.json();
    },
  });

  useEffect(() => {
    if (dietDay) {
      setWaterIntake(dietDay.waterIntake || 0);
    } else {
      setWaterIntake(0);
    }
  }, [dietDay]);

  const createDietDayMutation = useMutation({
    mutationFn: async (data: { date: string; waterIntake: number }) => {
      return apiRequest('POST', '/api/diet', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/diet', currentDate] });
    },
  });

  const updateDietDayMutation = useMutation({
    mutationFn: async (data: { id: number; waterIntake?: number; totalCalories?: number; totalProtein?: number; totalCarbs?: number; totalFat?: number }) => {
      const { id, ...updateData } = data;
      return apiRequest('PUT', `/api/diet/${id}`, updateData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/diet', currentDate] });
    },
  });

  const createMealMutation = useMutation({
    mutationFn: async (data: { dietDayId: number; type: string; time: string; notes?: string }) => {
      return apiRequest('POST', '/api/diet/meals', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/diet', currentDate] });
      toast({ title: "Meal added successfully" });
    },
  });

  const createMealItemMutation = useMutation({
    mutationFn: async (data: { dietMealId: number; name: string; quantity: string; calories: number; protein: number; carbs: number; fat: number }) => {
      return apiRequest('POST', '/api/diet/meals/items', data);
    },
    onSuccess: async () => {
      await queryClient.invalidateQueries({ queryKey: ['/api/diet', currentDate] });

      if (dietDay) {
        const updatedDiet = await queryClient.fetchQuery({ 
          queryKey: ['/api/diet', currentDate],
          queryFn: async () => {
            const response = await fetch(`/api/diet/${currentDate}`, {
              credentials: 'include',
            });
            return response.json();
          },
        });

        if (updatedDiet) {
          await updateDietDayMutation.mutateAsync({
            id: dietDay.id,
            totalCalories: updatedDiet.totalCalories,
            totalProtein: updatedDiet.totalProtein,
            totalCarbs: updatedDiet.totalCarbs,
            totalFat: updatedDiet.totalFat,
          });
        }
      }

      toast({ title: "Food item added successfully" });
    },
  });

  const deleteMealItemMutation = useMutation({
    mutationFn: async (itemId: number) => {
      return apiRequest('DELETE', `/api/diet/meals/items/${itemId}`);
    },
    onSuccess: async () => {
      await queryClient.invalidateQueries({ queryKey: ['/api/diet', currentDate] });

      if (dietDay) {
        const updatedDiet = await queryClient.fetchQuery({ 
          queryKey: ['/api/diet', currentDate],
          queryFn: async () => {
            const response = await fetch(`/api/diet/${currentDate}`, {
              credentials: 'include',
            });
            return response.json();
          },
        });

        if (updatedDiet) {
          await updateDietDayMutation.mutateAsync({
            id: dietDay.id,
            totalCalories: updatedDiet.totalCalories,
            totalProtein: updatedDiet.totalProtein,
            totalCarbs: updatedDiet.totalCarbs,
            totalFat: updatedDiet.totalFat,
          });
        }
      }

      toast({ title: "Food item removed successfully" });
    },
  });

  const mealForm = useForm<MealFormValues>({
    resolver: zodResolver(mealSchema),
    defaultValues: {
      type: "breakfast",
      time: "",
      notes: "",
    },
  });

  const mealItemForm = useForm<MealItemFormValues>({
    resolver: zodResolver(mealItemSchema),
    defaultValues: {
      name: "",
      quantity: "",
      calories: 0,
      protein: 0,
      carbs: 0,
      fat: 0,
    },
  });

  const ensureDietDay = async () => {
    if (!dietDay) {
      await createDietDayMutation.mutateAsync({
        date: currentDate,
        waterIntake: 0,
      });
      const newDietDay = await queryClient.fetchQuery({ 
        queryKey: ['/api/diet', currentDate],
        queryFn: async () => {
          const response = await fetch(`/api/diet/${currentDate}`, {
            credentials: 'include',
          });
          return response.json();
        },
      });
      return newDietDay;
    }
    return dietDay;
  };

  const handleAddMeal = async (data: MealFormValues) => {
    const day = await ensureDietDay();
    if (!day) return;

    await createMealMutation.mutateAsync({
      dietDayId: day.id,
      type: data.type,
      time: data.time,
      notes: data.notes,
    });

    setIsAddingMeal(false);
    mealForm.reset();
  };

  const handleAddMealItem = async (data: MealItemFormValues) => {
    if (!activeMealId) return;

    await createMealItemMutation.mutateAsync({
      dietMealId: activeMealId,
      ...data,
    });

    setIsAddingMealItem(false);
    mealItemForm.reset();
  };

  const handleWaterIntakeChange = async (value: number[]) => {
    const intake = value[0];
    setWaterIntake(intake);

    const day = await ensureDietDay();
    if (day) {
      await updateDietDayMutation.mutateAsync({
        id: day.id,
        waterIntake: intake,
      });
    }
  };

  const handleRemoveMealItem = async (itemId: number) => {
    await deleteMealItemMutation.mutateAsync(itemId);
  };

  const calculateTotals = (meals: Meal[]) => {
    let totalCalories = 0;
    let totalProtein = 0;
    let totalCarbs = 0;
    let totalFat = 0;

    meals.forEach(meal => {
      meal.items.forEach(item => {
        totalCalories += item.calories;
        totalProtein += item.protein;
        totalCarbs += item.carbs;
        totalFat += item.fat;
      });
    });

    return { totalCalories, totalProtein, totalCarbs, totalFat };
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('en-IN', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    }).format(date);
  };

  const totals = dietDay ? calculateTotals(dietDay.meals) : { totalCalories: 0, totalProtein: 0, totalCarbs: 0, totalFat: 0 };

  return (
    <Layout title="Daily Diet Routine">
      <div className="mb-6">
        <h1 className="text-2xl font-bold">Daily Diet Routine</h1>
        <p className="text-muted-foreground">Track your meals and nutrition intake</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2">
          <Card>
            <CardHeader className="pb-3">
              <div className="flex justify-between items-center">
                <div>
                  <CardTitle>{formatDate(currentDate)}</CardTitle>
                  <CardDescription>Track your daily meals and nutrition</CardDescription>
                </div>
                <div>
                  <Input
                    type="date"
                    value={currentDate}
                    onChange={(e) => setCurrentDate(e.target.value)}
                    className="max-w-[200px]"
                    data-testid="input-date"
                  />
                </div>
              </div>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="space-y-4">
                  <Skeleton className="h-32 w-full" />
                  <Skeleton className="h-32 w-full" />
                </div>
              ) : !dietDay || dietDay.meals.length === 0 ? (
                <div className="text-center py-12" data-testid="empty-diet">
                  <i className="ri-restaurant-line text-5xl text-gray-400 mb-3"></i>
                  <p className="text-gray-500 text-lg mb-1">No diet booked</p>
                  <p className="text-gray-400 text-sm mb-4">Start tracking your meals by adding your first meal</p>
                  <Button onClick={() => setIsAddingMeal(true)} data-testid="button-add-first-meal">
                    <Plus className="h-4 w-4 mr-2" />
                    Add Your First Meal
                  </Button>
                </div>
              ) : (
                <Tabs defaultValue="meals" className="space-y-4">
                  <TabsList>
                    <TabsTrigger value="meals">Meals</TabsTrigger>
                    <TabsTrigger value="nutrition">Nutrition</TabsTrigger>
                    <TabsTrigger value="water">Water Intake</TabsTrigger>
                  </TabsList>

                  <TabsContent value="meals" className="space-y-4">
                    {dietDay.meals.map((meal) => (
                      <Card key={meal.id} className={`border ${activeMealId === meal.id ? 'border-primary' : ''}`}>
                        <CardHeader className="pb-2">
                          <div className="flex justify-between items-center">
                            <div>
                              <CardTitle className="text-lg capitalize">{meal.type}</CardTitle>
                              <CardDescription>Time: {meal.time}</CardDescription>
                            </div>
                            <Button 
                              variant="ghost" 
                              size="sm" 
                              onClick={() => {
                                setActiveMealId(activeMealId === meal.id ? null : meal.id);
                                setIsAddingMealItem(false);
                              }}
                              data-testid={`button-expand-meal-${meal.id}`}
                            >
                              {activeMealId === meal.id ? 'Close' : 'Expand'}
                            </Button>
                          </div>
                        </CardHeader>
                        {activeMealId === meal.id && (
                          <>
                            <CardContent className="pb-2">
                              <div className="space-y-2">
                                {meal.items.length > 0 ? (
                                  <>
                                    <div className="grid grid-cols-12 gap-2 text-xs font-medium text-muted-foreground pb-1">
                                      <div className="col-span-4">Food Item</div>
                                      <div className="col-span-2">Quantity</div>
                                      <div className="col-span-1">Cal</div>
                                      <div className="col-span-1">Pro</div>
                                      <div className="col-span-1">Carb</div>
                                      <div className="col-span-1">Fat</div>
                                      <div className="col-span-2"></div>
                                    </div>
                                    {meal.items.map((item) => (
                                      <div key={item.id} className="grid grid-cols-12 gap-2 items-center text-sm py-2 border-b last:border-0" data-testid={`meal-item-${item.id}`}>
                                        <div className="col-span-4 font-medium">{item.name}</div>
                                        <div className="col-span-2 text-muted-foreground">{item.quantity}</div>
                                        <div className="col-span-1">{item.calories}</div>
                                        <div className="col-span-1">{item.protein}g</div>
                                        <div className="col-span-1">{item.carbs}g</div>
                                        <div className="col-span-1">{item.fat}g</div>
                                        <div className="col-span-2 text-right">
                                          <Button
                                            variant="ghost"
                                            size="icon"
                                            onClick={() => handleRemoveMealItem(item.id)}
                                            data-testid={`button-remove-item-${item.id}`}
                                          >
                                            <Trash2 className="h-4 w-4 text-red-500" />
                                          </Button>
                                        </div>
                                      </div>
                                    ))}
                                  </>
                                ) : (
                                  <div className="text-center py-6 text-muted-foreground">
                                    No food items added to this meal yet.
                                  </div>
                                )}

                                {!isAddingMealItem ? (
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    className="mt-2"
                                    onClick={() => setIsAddingMealItem(true)}
                                    data-testid="button-add-food-item"
                                  >
                                    <Plus className="h-4 w-4 mr-2" />
                                    Add Food Item
                                  </Button>
                                ) : (
                                  <Card className="mt-4">
                                    <CardHeader className="pb-2">
                                      <CardTitle className="text-md">Add Food Item</CardTitle>
                                    </CardHeader>
                                    <CardContent>
                                      <Form {...mealItemForm}>
                                        <form onSubmit={mealItemForm.handleSubmit(handleAddMealItem)} className="space-y-4">
                                          <div className="grid grid-cols-2 gap-4">
                                            <FormField
                                              control={mealItemForm.control}
                                              name="name"
                                              render={({ field }) => (
                                                <FormItem>
                                                  <FormLabel>Food Item</FormLabel>
                                                  <FormControl>
                                                    <Input placeholder="E.g. Brown Rice" {...field} data-testid="input-food-name" />
                                                  </FormControl>
                                                  <FormMessage />
                                                </FormItem>
                                              )}
                                            />

                                            <FormField
                                              control={mealItemForm.control}
                                              name="quantity"
                                              render={({ field }) => (
                                                <FormItem>
                                                  <FormLabel>Quantity</FormLabel>
                                                  <FormControl>
                                                    <Input placeholder="E.g. 1 cup" {...field} data-testid="input-quantity" />
                                                  </FormControl>
                                                  <FormMessage />
                                                </FormItem>
                                              )}
                                            />
                                          </div>

                                          <div className="grid grid-cols-4 gap-4">
                                            <FormField
                                              control={mealItemForm.control}
                                              name="calories"
                                              render={({ field }) => (
                                                <FormItem>
                                                  <FormLabel>Calories</FormLabel>
                                                  <FormControl>
                                                    <Input 
                                                      type="number" 
                                                      placeholder="0" 
                                                      {...field}
                                                      onChange={(e) => field.onChange(Number(e.target.value))}
                                                      data-testid="input-calories"
                                                    />
                                                  </FormControl>
                                                  <FormMessage />
                                                </FormItem>
                                              )}
                                            />

                                            <FormField
                                              control={mealItemForm.control}
                                              name="protein"
                                              render={({ field }) => (
                                                <FormItem>
                                                  <FormLabel>Protein (g)</FormLabel>
                                                  <FormControl>
                                                    <Input 
                                                      type="number" 
                                                      placeholder="0" 
                                                      {...field}
                                                      onChange={(e) => field.onChange(Number(e.target.value))}
                                                      data-testid="input-protein"
                                                    />
                                                  </FormControl>
                                                  <FormMessage />
                                                </FormItem>
                                              )}
                                            />

                                            <FormField
                                              control={mealItemForm.control}
                                              name="carbs"
                                              render={({ field }) => (
                                                <FormItem>
                                                  <FormLabel>Carbs (g)</FormLabel>
                                                  <FormControl>
                                                    <Input 
                                                      type="number" 
                                                      placeholder="0" 
                                                      {...field}
                                                      onChange={(e) => field.onChange(Number(e.target.value))}
                                                      data-testid="input-carbs"
                                                    />
                                                  </FormControl>
                                                  <FormMessage />
                                                </FormItem>
                                              )}
                                            />

                                            <FormField
                                              control={mealItemForm.control}
                                              name="fat"
                                              render={({ field }) => (
                                                <FormItem>
                                                  <FormLabel>Fat (g)</FormLabel>
                                                  <FormControl>
                                                    <Input 
                                                      type="number" 
                                                      placeholder="0" 
                                                      {...field}
                                                      onChange={(e) => field.onChange(Number(e.target.value))}
                                                      data-testid="input-fat"
                                                    />
                                                  </FormControl>
                                                  <FormMessage />
                                                </FormItem>
                                              )}
                                            />
                                          </div>

                                          <div className="flex justify-end space-x-2">
                                            <Button
                                              type="button"
                                              variant="outline"
                                              onClick={() => setIsAddingMealItem(false)}
                                              data-testid="button-cancel-food-item"
                                            >
                                              Cancel
                                            </Button>
                                            <Button type="submit" data-testid="button-submit-food-item">Add Food Item</Button>
                                          </div>
                                        </form>
                                      </Form>
                                    </CardContent>
                                  </Card>
                                )}
                              </div>
                            </CardContent>
                            {meal.notes && (
                              <CardFooter>
                                <p className="text-sm text-muted-foreground">
                                  <span className="font-medium">Notes:</span> {meal.notes}
                                </p>
                              </CardFooter>
                            )}
                          </>
                        )}
                      </Card>
                    ))}

                    {!isAddingMeal ? (
                      <Button
                        variant="outline"
                        onClick={() => setIsAddingMeal(true)}
                        data-testid="button-add-new-meal"
                      >
                        <Plus className="h-4 w-4 mr-2" />
                        Add New Meal
                      </Button>
                    ) : (
                      <Card>
                        <CardHeader>
                          <CardTitle>Add New Meal</CardTitle>
                        </CardHeader>
                        <CardContent>
                          <Form {...mealForm}>
                            <form onSubmit={mealForm.handleSubmit(handleAddMeal)} className="space-y-4">
                              <div className="grid grid-cols-2 gap-4">
                                <FormField
                                  control={mealForm.control}
                                  name="type"
                                  render={({ field }) => (
                                    <FormItem>
                                      <FormLabel>Meal Type</FormLabel>
                                      <Select
                                        onValueChange={field.onChange}
                                        defaultValue={field.value}
                                      >
                                        <FormControl>
                                          <SelectTrigger data-testid="select-meal-type">
                                            <SelectValue placeholder="Select meal type" />
                                          </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                          <SelectItem value="breakfast">Breakfast</SelectItem>
                                          <SelectItem value="lunch">Lunch</SelectItem>
                                          <SelectItem value="dinner">Dinner</SelectItem>
                                          <SelectItem value="snack">Snack</SelectItem>
                                        </SelectContent>
                                      </Select>
                                      <FormMessage />
                                    </FormItem>
                                  )}
                                />

                                <FormField
                                  control={mealForm.control}
                                  name="time"
                                  render={({ field }) => (
                                    <FormItem>
                                      <FormLabel>Time</FormLabel>
                                      <FormControl>
                                        <Input type="time" {...field} data-testid="input-meal-time" />
                                      </FormControl>
                                      <FormMessage />
                                    </FormItem>
                                  )}
                                />
                              </div>

                              <FormField
                                control={mealForm.control}
                                name="notes"
                                render={({ field }) => (
                                  <FormItem>
                                    <FormLabel>Notes (Optional)</FormLabel>
                                    <FormControl>
                                      <Textarea
                                        placeholder="Any notes about this meal?"
                                        className="resize-none"
                                        {...field}
                                        data-testid="input-meal-notes"
                                      />
                                    </FormControl>
                                    <FormMessage />
                                  </FormItem>
                                )}
                              />

                              <div className="flex justify-end space-x-2">
                                <Button
                                  type="button"
                                  variant="outline"
                                  onClick={() => setIsAddingMeal(false)}
                                  data-testid="button-cancel-meal"
                                >
                                  Cancel
                                </Button>
                                <Button type="submit" data-testid="button-submit-meal">Add Meal</Button>
                              </div>
                            </form>
                          </Form>
                        </CardContent>
                      </Card>
                    )}
                  </TabsContent>

                  <TabsContent value="nutrition">
                    <div className="space-y-6">
                      <div className="grid grid-cols-4 gap-4">
                        <Card className="p-4">
                          <div className="text-center">
                            <div className="text-2xl font-bold" data-testid="total-calories">{totals.totalCalories}</div>
                            <div className="text-sm text-muted-foreground">Calories</div>
                          </div>
                        </Card>
                        <Card className="p-4">
                          <div className="text-center">
                            <div className="text-2xl font-bold" data-testid="total-protein">{totals.totalProtein}g</div>
                            <div className="text-sm text-muted-foreground">Protein</div>
                          </div>
                        </Card>
                        <Card className="p-4">
                          <div className="text-center">
                            <div className="text-2xl font-bold" data-testid="total-carbs">{totals.totalCarbs}g</div>
                            <div className="text-sm text-muted-foreground">Carbs</div>
                          </div>
                        </Card>
                        <Card className="p-4">
                          <div className="text-center">
                            <div className="text-2xl font-bold" data-testid="total-fat">{totals.totalFat.toFixed(1)}g</div>
                            <div className="text-sm text-muted-foreground">Fat</div>
                          </div>
                        </Card>
                      </div>

                      {totals.totalCalories > 0 && (
                        <Card>
                          <CardHeader>
                            <CardTitle>Macro Distribution</CardTitle>
                          </CardHeader>
                          <CardContent>
                            <div className="space-y-4">
                              <div>
                                <div className="flex justify-between mb-1">
                                  <span className="text-sm font-medium">Protein</span>
                                  <span className="text-sm text-muted-foreground">
                                    {Math.round((totals.totalProtein * 4 / totals.totalCalories) * 100)}%
                                  </span>
                                </div>
                                <div className="w-full bg-gray-200 rounded-full h-2.5">
                                  <div
                                    className="bg-blue-500 h-2.5 rounded-full"
                                    style={{ width: `${(totals.totalProtein * 4 / totals.totalCalories) * 100}%` }}
                                  ></div>
                                </div>
                              </div>
                              <div>
                                <div className="flex justify-between mb-1">
                                  <span className="text-sm font-medium">Carbohydrates</span>
                                  <span className="text-sm text-muted-foreground">
                                    {Math.round((totals.totalCarbs * 4 / totals.totalCalories) * 100)}%
                                  </span>
                                </div>
                                <div className="w-full bg-gray-200 rounded-full h-2.5">
                                  <div
                                    className="bg-green-500 h-2.5 rounded-full"
                                    style={{ width: `${(totals.totalCarbs * 4 / totals.totalCalories) * 100}%` }}
                                  ></div>
                                </div>
                              </div>
                              <div>
                                <div className="flex justify-between mb-1">
                                  <span className="text-sm font-medium">Fat</span>
                                  <span className="text-sm text-muted-foreground">
                                    {Math.round((totals.totalFat * 9 / totals.totalCalories) * 100)}%
                                  </span>
                                </div>
                                <div className="w-full bg-gray-200 rounded-full h-2.5">
                                  <div
                                    className="bg-yellow-500 h-2.5 rounded-full"
                                    style={{ width: `${(totals.totalFat * 9 / totals.totalCalories) * 100}%` }}
                                  ></div>
                                </div>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      )}
                    </div>
                  </TabsContent>

                  <TabsContent value="water">
                    <Card>
                      <CardHeader>
                        <CardTitle>Water Intake</CardTitle>
                        <CardDescription>Track your daily water consumption</CardDescription>
                      </CardHeader>
                      <CardContent className="space-y-6">
                        <div className="flex items-center justify-center">
                          <div className="text-center">
                            <div className="text-4xl font-bold text-blue-500" data-testid="water-intake">{waterIntake} ml</div>
                            <div className="text-sm text-muted-foreground mt-2">Today's water intake</div>
                          </div>
                        </div>

                        <div className="px-4">
                          <div className="flex justify-between mb-2">
                            <span>0 ml</span>
                            <span>3000 ml</span>
                          </div>
                          <Slider
                            defaultValue={[waterIntake]}
                            value={[waterIntake]}
                            max={3000}
                            step={100}
                            onValueChange={handleWaterIntakeChange}
                            data-testid="slider-water-intake"
                          />
                        </div>

                        <div className="grid grid-cols-4 gap-2">
                          {[250, 500, 750, 1000].map((amount) => (
                            <Button
                              key={amount}
                              variant="outline"
                              size="sm"
                              onClick={() => handleWaterIntakeChange([Math.min(waterIntake + amount, 3000)])}
                              data-testid={`button-add-water-${amount}`}
                            >
                              +{amount} ml
                            </Button>
                          ))}
                        </div>

                        <div className="bg-blue-50 p-4 rounded-lg">
                          <div className="flex space-x-3">
                            <AlertCircle className="h-5 w-5 text-blue-500 flex-shrink-0 mt-0.5" />
                            <div>
                              <h4 className="font-medium text-blue-800">Hydration Tip</h4>
                              <p className="text-sm text-blue-700 mt-1">
                                It's recommended to drink at least 2000ml of water daily for optimal health. Staying hydrated helps maintain energy levels, supports digestion, and improves skin health.
                              </p>
                            </div>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  </TabsContent>
                </Tabs>
              )}
            </CardContent>
          </Card>
        </div>

        <div>
          <Card className="sticky top-4">
            <CardHeader>
              <CardTitle>Daily Summary</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span className="text-sm text-muted-foreground">Total Calories</span>
                  <span className="text-sm font-medium" data-testid="summary-calories">{totals.totalCalories} kcal</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm text-muted-foreground">Protein</span>
                  <span className="text-sm font-medium" data-testid="summary-protein">{totals.totalProtein}g</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm text-muted-foreground">Carbs</span>
                  <span className="text-sm font-medium" data-testid="summary-carbs">{totals.totalCarbs}g</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm text-muted-foreground">Fat</span>
                  <span className="text-sm font-medium" data-testid="summary-fat">{totals.totalFat.toFixed(1)}g</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm text-muted-foreground">Water Intake</span>
                  <span className="text-sm font-medium" data-testid="summary-water">{waterIntake} ml</span>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/doctors.tsx.content:
import Layout from "@/components/layout/Layout";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { useState, useEffect } from "react";
import { Doctor } from "@shared/schema";
import { Badge } from "@/components/ui/badge";

export default function Doctors() {
  const { data, isLoading } = useQuery({ 
    queryKey: ["/api/doctors"]
  });

  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedSpecialty, setSelectedSpecialty] = useState<string | null>(null);

  useEffect(() => {
    if (data) {
      setDoctors(data);
    }
  }, [data]);

  // Get unique specialties for filter
  const specialties = doctors.length 
    ? [...new Set(doctors.map(doctor => doctor.specialty))]
    : [];

  const filteredDoctors = doctors.filter(doctor => {
    const matchesSearch = (
      doctor.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      doctor.specialty.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (doctor.hospital && doctor.hospital.toLowerCase().includes(searchTerm.toLowerCase()))
    );

    const matchesSpecialty = !selectedSpecialty || doctor.specialty === selectedSpecialty;

    return matchesSearch && matchesSpecialty;
  });

  const renderStars = (rating?: number) => {
    if (!rating) return null;

    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<i key={`full-${i}`} className="ri-star-fill text-yellow-400"></i>);
    }

    if (hasHalfStar) {
      stars.push(<i key="half" className="ri-star-half-fill text-yellow-400"></i>);
    }

    const emptyStars = 5 - stars.length;
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<i key={`empty-${i}`} className="ri-star-line text-yellow-400"></i>);
    }

    return stars;
  };

  return (
    <Layout title="Doctors">
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Find Doctors</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="relative mb-4">
            <Input
              className="pl-10"
              placeholder="Search doctors by name, specialty, or hospital..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
              <i className="ri-search-line text-gray-400"></i>
            </div>
          </div>

          {specialties.length > 0 && (
            <div className="flex flex-wrap gap-2 mt-4">
              <Badge 
                variant={!selectedSpecialty ? "default" : "outline"}
                className="cursor-pointer"
                onClick={() => setSelectedSpecialty(null)}
              >
                All Specialties
              </Badge>
              {specialties.map(specialty => (
                <Badge 
                  key={specialty}
                  variant={selectedSpecialty === specialty ? "default" : "outline"}
                  className="cursor-pointer"
                  onClick={() => setSelectedSpecialty(specialty)}
                >
                  {specialty}
                </Badge>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {isLoading ? (
          Array(6).fill(0).map((_, i) => (
            <Skeleton key={i} className="h-64 w-full" />
          ))
        ) : filteredDoctors.length > 0 ? (
          filteredDoctors.map((doctor) => (
            <Card key={doctor.id} className="overflow-hidden">
              <div className="p-6">
                <div className="flex items-start">
                  <div className="flex-shrink-0">
                    {doctor.profileImage ? (
                      <img 
                        src={doctor.profileImage} 
                        alt={doctor.name}
                        className="h-16 w-16 rounded-full object-cover" 
                      />
                    ) : (
                      <div className="h-16 w-16 rounded-full bg-primary-100 flex items-center justify-center text-primary-700">
                        <i className="ri-user-heart-line text-2xl"></i>
                      </div>
                    )}
                  </div>
                  <div className="ml-4 flex-1">
                    <h3 className="text-lg font-medium text-gray-900">{doctor.name}</h3>
                    <p className="text-sm font-medium text-primary-600">{doctor.specialty}</p>
                    {doctor.hospital && (
                      <p className="mt-1 text-sm text-gray-500">
                        <i className="ri-hospital-line mr-1"></i>
                        {doctor.hospital}
                      </p>
                    )}
                    {doctor.rating && (
                      <div className="mt-1 flex items-center">
                        <div className="flex items-center">
                          {renderStars(doctor.rating)}
                        </div>
                        <span className="ml-1 text-sm text-gray-500">
                          {doctor.rating.toFixed(1)}
                        </span>
                      </div>
                    )}
                    {doctor.availableDays && doctor.availableDays.length > 0 && (
                      <div className="mt-2">
                        <p className="text-xs text-gray-500">Available on:</p>
                        <div className="flex flex-wrap gap-1 mt-1">
                          {doctor.availableDays.map(day => (
                            <span key={day} className="px-2 py-0.5 bg-gray-100 text-gray-700 text-xs rounded-full">
                              {day}
                            </span>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
                <div className="mt-4 grid grid-cols-2 gap-2">
                  <Button variant="outline" className="flex justify-center items-center">
                    <i className="ri-phone-line mr-1"></i>
                    Contact
                  </Button>
                  <Button className="flex justify-center items-center">
                    <i className="ri-calendar-line mr-1"></i>
                    Book Appointment
                  </Button>
                </div>
              </div>
            </Card>
          ))
        ) : (
          <div className="col-span-full text-center py-12">
            <i className="ri-user-search-line text-5xl text-gray-300"></i>
            <p className="mt-4 text-gray-500">No doctors found matching your search criteria</p>
          </div>
        )}
      </div>
    </Layout>
  );
}

client/src/pages/hospitals.tsx.content:
import Layout from "@/components/layout/Layout";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { MapPin, Phone, Mail, Plus, Navigation, Building2, Star } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface Hospital {
  id: number;
  name: string;
  address: string;
  city?: string;
  state?: string;
  phoneNumber: string;
  email?: string;
  rating?: number;
  logo?: string;
  latitude?: string;
  longitude?: string;
  distance?: number;
  departments?: string[];
  services?: string[];
  emergencyServices?: boolean;
  beds?: number;
  established?: string;
  website?: string;
}

const hospitalFormSchema = z.object({
  name: z.string().min(3, "Hospital name must be at least 3 characters"),
  address: z.string().min(5, "Address is required"),
  city: z.string().min(2, "City is required"),
  state: z.string().min(2, "State is required"),
  phoneNumber: z.string().min(10, "Valid phone number is required"),
  email: z.string().email("Valid email is required").optional().or(z.literal("")),
  latitude: z.string().optional(),
  longitude: z.string().optional(),
  departments: z.string().optional(),
  services: z.string().optional(),
  emergencyServices: z.boolean().default(true),
  beds: z.string().optional(),
  established: z.string().optional(),
  website: z.string().url("Valid URL is required").optional().or(z.literal("")),
});

type HospitalFormData = z.infer<typeof hospitalFormSchema>;

export default function Hospitals() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [_, navigate] = useLocation();
  const [hospitals, setHospitals] = useState<Hospital[]>([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const [userCity, setUserCity] = useState<string>("");
  const [filterMode, setFilterMode] = useState<"all" | "city" | "nearby">("all");

  const form = useForm<HospitalFormData>({
    resolver: zodResolver(hospitalFormSchema),
    defaultValues: {
      name: "",
      address: "",
      city: "",
      state: "",
      phoneNumber: "",
      email: "",
      latitude: "",
      longitude: "",
      departments: "",
      services: "",
      emergencyServices: true,
      beds: "",
      established: "",
      website: "",
    },
  });

  // Fetch all hospitals
  const { data: allHospitals, isLoading } = useQuery({ 
    queryKey: ["/api/hospitals"]
  });

  // Search hospitals mutation
  const searchMutation = useMutation({
    mutationFn: async (params: { city?: string; latitude?: number; longitude?: number; maxDistance?: number }) => {
      return await apiRequest("POST", "/api/hospitals/search", params);
    },
    onSuccess: (data: any) => {
      if (data.hospitals) {
        setHospitals(data.hospitals);
      }
    },
  });

  // Create hospital mutation
  const createHospitalMutation = useMutation({
    mutationFn: async (data: HospitalFormData) => {
      const hospitalData = {
        ...data,
        departments: data.departments ? data.departments.split(",").map(d => d.trim()) : [],
        services: data.services ? data.services.split(",").map(s => s.trim()) : [],
        beds: data.beds ? parseInt(data.beds) : undefined,
        email: data.email || undefined,
        website: data.website || undefined,
      };
      return await apiRequest("POST", "/api/hospitals", hospitalData);
    },
    onSuccess: () => {
      toast({
        title: "Hospital Added",
        description: "Hospital has been added successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/hospitals"] });
      setIsAddDialogOpen(false);
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Add Hospital",
        description: error.message || "Could not add hospital.",
        variant: "destructive",
      });
    },
  });

  // Get user location and search
  const handleLocationSearch = () => {
    setIsGettingLocation(true);

    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;

          // Search by location
          searchMutation.mutate({ latitude, longitude, maxDistance: 50 });
          setFilterMode("nearby");

          // Update user location in background
          try {
            await apiRequest("PATCH", "/api/user/location", {
              latitude: latitude.toString(),
              longitude: longitude.toString(),
              city: userCity || "Unknown",
              state: "Unknown"
            });
          } catch (error) {
            console.error("Failed to update user location:", error);
          }

          setIsGettingLocation(false);
        },
        (error) => {
          console.error("Error getting location:", error);
          toast({
            title: "Location Error",
            description: "Unable to retrieve your location. Please enable location services.",
            variant: "destructive",
          });
          setIsGettingLocation(false);
        },
        { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
      );
    } else {
      toast({
        title: "Location Not Supported",
        description: "Your browser doesn't support geolocation.",
        variant: "destructive",
      });
      setIsGettingLocation(false);
    }
  };

  // Get location for form
  const handleGetFormLocation = () => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          form.setValue("latitude", latitude.toString());
          form.setValue("longitude", longitude.toString());
          toast({
            title: "Location Captured",
            description: "Hospital location has been set to your current position.",
          });
        },
        (error) => {
          toast({
            title: "Location Error",
            description: "Unable to get location. Please enter manually.",
            variant: "destructive",
          });
        },
        { enableHighAccuracy: true }
      );
    }
  };

  // Initialize hospitals and check user city
  useEffect(() => {
    if (allHospitals) {
      setHospitals(allHospitals);
    }
  }, [allHospitals]);

  // Check if user has city set
  useEffect(() => {
    if (user?.city) {
      setUserCity(user.city);
    }
  }, [user]);

  // Filter by user city
  const handleCityFilter = () => {
    if (userCity) {
      searchMutation.mutate({ city: userCity });
      setFilterMode("city");
    } else {
      toast({
        title: "No City Set",
        description: "Please use location search to set your city first.",
        variant: "destructive",
      });
    }
  };

  const handleShowAll = () => {
    if (allHospitals) {
      setHospitals(allHospitals);
      setFilterMode("all");
    }
  };

  const onSubmit = (data: HospitalFormData) => {
    createHospitalMutation.mutate(data);
  };

  const renderStars = (rating?: number) => {
    if (!rating) return null;
    const stars = [];
    const fullStars = Math.floor(rating);

    for (let i = 0; i < fullStars; i++) {
      stars.push(<Star key={`full-${i}`} className="h-4 w-4 fill-yellow-400 text-yellow-400" />);
    }

    const emptyStars = 5 - stars.length;
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<Star key={`empty-${i}`} className="h-4 w-4 text-gray-300" />);
    }

    return <div className="flex items-center gap-1">{stars}</div>;
  };

  const filteredHospitals = hospitals.filter(hospital => 
    hospital.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    hospital.address.toLowerCase().includes(searchTerm.toLowerCase()) ||
    hospital.city?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Layout title="Hospitals">
      <div className="space-y-6">
        {/* Header with filters and add button */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>Find Hospitals</CardTitle>
                <CardDescription>
                  {userCity && `Showing hospitals ${filterMode === "city" ? `in ${userCity}` : filterMode === "nearby" ? "near you" : "all"}`}
                </CardDescription>
              </div>
              <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
                <DialogTrigger asChild>
                  <Button data-testid="button-add-hospital">
                    <Plus className="h-4 w-4 mr-2" />
                    Add Hospital
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>Register New Hospital</DialogTitle>
                    <DialogDescription>
                      Add a new hospital to the directory with complete details.
                    </DialogDescription>
                  </DialogHeader>
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                      <FormField
                        control={form.control}
                        name="name"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Hospital Name *</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter hospital name" {...field} data-testid="input-hospital-name" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="address"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Full Address *</FormLabel>
                            <FormControl>
                              <Input placeholder="Street address" {...field} data-testid="input-address" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="city"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>City *</FormLabel>
                              <FormControl>
                                <Input placeholder="City" {...field} data-testid="input-city" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="state"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>State *</FormLabel>
                              <FormControl>
                                <Input placeholder="State" {...field} data-testid="input-state" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="phoneNumber"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Phone Number *</FormLabel>
                              <FormControl>
                                <Input placeholder="Phone number" {...field} data-testid="input-phone" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="email"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Email</FormLabel>
                              <FormControl>
                                <Input placeholder="Email" {...field} data-testid="input-email" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="space-y-2">
                        <FormLabel>Location Coordinates</FormLabel>
                        <div className="grid grid-cols-2 gap-4">
                          <FormField
                            control={form.control}
                            name="latitude"
                            render={({ field }) => (
                              <FormItem>
                                <FormControl>
                                  <Input placeholder="Latitude" {...field} data-testid="input-latitude" />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={form.control}
                            name="longitude"
                            render={({ field }) => (
                              <FormItem>
                                <FormControl>
                                  <Input placeholder="Longitude" {...field} data-testid="input-longitude" />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>
                        <Button 
                          type="button" 
                          variant="outline" 
                          size="sm"
                          onClick={handleGetFormLocation}
                          data-testid="button-get-location"
                        >
                          <Navigation className="h-4 w-4 mr-2" />
                          Use Current Location
                        </Button>
                      </div>

                      <FormField
                        control={form.control}
                        name="departments"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Departments (comma-separated)</FormLabel>
                            <FormControl>
                              <Input placeholder="e.g., Cardiology, Neurology, Pediatrics" {...field} data-testid="input-departments" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="services"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Services (comma-separated)</FormLabel>
                            <FormControl>
                              <Input placeholder="e.g., Emergency, Surgery, Imaging" {...field} data-testid="input-services" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="grid grid-cols-3 gap-4">
                        <FormField
                          control={form.control}
                          name="beds"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Beds</FormLabel>
                              <FormControl>
                                <Input type="number" placeholder="Number of beds" {...field} data-testid="input-beds" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="established"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Established</FormLabel>
                              <FormControl>
                                <Input placeholder="Year" {...field} data-testid="input-established" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="website"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Website</FormLabel>
                              <FormControl>
                                <Input placeholder="https://" {...field} data-testid="input-website" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="flex justify-end gap-2 pt-4">
                        <Button 
                          type="button" 
                          variant="outline" 
                          onClick={() => setIsAddDialogOpen(false)}
                          data-testid="button-cancel"
                        >
                          Cancel
                        </Button>
                        <Button 
                          type="submit" 
                          disabled={createHospitalMutation.isPending}
                          data-testid="button-submit-hospital"
                        >
                          {createHospitalMutation.isPending ? "Adding..." : "Add Hospital"}
                        </Button>
                      </div>
                    </form>
                  </Form>
                </DialogContent>
              </Dialog>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <Input
              className="max-w-md"
              placeholder="Search hospitals by name, address, or city..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              data-testid="input-search-hospitals"
            />

            <div className="flex gap-2">
              <Button 
                variant={filterMode === "all" ? "default" : "outline"}
                onClick={handleShowAll}
                data-testid="button-show-all"
              >
                Show All
              </Button>
              <Button 
                variant={filterMode === "city" ? "default" : "outline"}
                onClick={handleCityFilter}
                disabled={!userCity}
                data-testid="button-filter-city"
              >
                <Building2 className="h-4 w-4 mr-2" />
                My City {userCity && `(${userCity})`}
              </Button>
              <Button 
                variant={filterMode === "nearby" ? "default" : "outline"}
                onClick={handleLocationSearch}
                disabled={isGettingLocation || searchMutation.isPending}
                data-testid="button-filter-nearby"
              >
                <MapPin className="h-4 w-4 mr-2" />
                {isGettingLocation ? "Getting Location..." : "Nearby"}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Hospital Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {isLoading || searchMutation.isPending ? (
            Array(6).fill(0).map((_, i) => (
              <Skeleton key={i} className="h-80 w-full" />
            ))
          ) : filteredHospitals.length > 0 ? (
            filteredHospitals.map((hospital) => (
              <Card key={hospital.id} className="overflow-hidden hover:shadow-lg transition-shadow" data-testid={`card-hospital-${hospital.id}`}>
                <CardHeader className="pb-3">
                  <div className="flex items-start gap-3">
                    <div className="h-12 w-12 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0">
                      <Building2 className="h-6 w-6 text-primary" />
                    </div>
                    <div className="flex-1 min-w-0">
                      <CardTitle className="text-lg line-clamp-1">{hospital.name}</CardTitle>
                      {hospital.rating && (
                        <div className="flex items-center gap-2 mt-1">
                          {renderStars(hospital.rating)}
                          <span className="text-sm text-gray-500">{hospital.rating.toFixed(1)}</span>
                        </div>
                      )}
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="space-y-2 text-sm">
                    <div className="flex items-start gap-2">
                      <MapPin className="h-4 w-4 text-gray-400 mt-0.5 flex-shrink-0" />
                      <span className="text-gray-600 line-clamp-2">
                        {hospital.address}
                        {hospital.city && hospital.state && `, ${hospital.city}, ${hospital.state}`}
                      </span>
                    </div>
                    <div className="flex items-center gap-2">
                      <Phone className="h-4 w-4 text-gray-400 flex-shrink-0" />
                      <span className="text-gray-600">{hospital.phoneNumber}</span>
                    </div>
                    {hospital.email && (
                      <div className="flex items-center gap-2">
                        <Mail className="h-4 w-4 text-gray-400 flex-shrink-0" />
                        <span className="text-gray-600 truncate">{hospital.email}</span>
                      </div>
                    )}
                    {hospital.distance !== undefined && (
                      <Badge variant="secondary" className="mt-2">
                        {hospital.distance.toFixed(1)} km away
                      </Badge>
                    )}
                  </div>

                  {hospital.emergencyServices && (
                    <Badge variant="destructive" className="text-xs">
                      Emergency Services Available
                    </Badge>
                  )}

                  <div className="grid grid-cols-2 gap-2 pt-2">
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => {
                        const address = encodeURIComponent(`${hospital.address}, ${hospital.city || ""}`);
                        window.open(`https://www.google.com/maps/search/?api=1&query=${address}`, '_blank');
                      }}
                      data-testid={`button-directions-${hospital.id}`}
                    >
                      <Navigation className="h-3 w-3 mr-1" />
                      Directions
                    </Button>
                    <Button 
                      size="sm"
                      onClick={() => navigate(`/appointments?hospitalId=${hospital.id}`)}
                      data-testid={`button-book-${hospital.id}`}
                    >
                      Book Visit
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="col-span-full text-center py-12">
              <Building2 className="h-16 w-16 mx-auto text-gray-300 mb-4" />
              <p className="text-gray-500 text-lg">No hospitals found</p>
              <p className="text-gray-400 text-sm mt-2">
                {searchTerm ? `No results for "${searchTerm}"` : "Try searching by location or add a new hospital"}
              </p>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}

